function validateForm(){const e=document.getElementById("email");const t=document.getElementById("emailError");const n=/^[a-z0-9._%+-]+@[a-z0-9.-]+\.[a-z]{2,}$/;if(!n.test(e.value)){t.style.display="block";e.focus();return false}t.style.display="none";return true}window.onload=function(){document.getElementById("email").focus()};