function validateForm(){const t=document.getElementById("new_password").value;const e=document.getElementById("confirm_password").value;const n=document.getElementById("password-error");const o=document.getElementById("confirm-error");n.textContent="";o.textContent="";let s=true;if(t.length<8){n.textContent="비밀번호는 최소 8자리 이상이어야 합니다.";s=false}if(!/[A-Z]/.test(t)){n.textContent+=" 대문자를 1개 이상 포함해야 합니다.";s=false}const r=(t.match(/\d/g)||[]).length;if(r<3){n.textContent+=" 숫자를 3자리 이상 포함해야 합니다.";s=false}if(t!==e){o.textContent="비밀번호가 일치하지 않습니다.";s=false}return s}