document.getElementById("sendVerificationBtn").addEventListener("click",function(){const e=document.getElementById("email").value;const t=document.getElementById("password").value;const n=new FormData;n.append("email",e);n.append("password",t);fetch("{{ url_for('main.send_withdrawal_verification') }}",{method:"POST",body:n}).then(e=>e.json()).then(e=>{if(e.success){alert("인증 코드가 발송되었습니다.")}else{alert(e.message||"인증 코드 발송에 실패했습니다.")}}).catch(e=>{console.error("Error:",e);alert("오류가 발생했습니다.")})});